void DrawLevel(u8 LevelNumber)
{
	u8 iLoopX;
	u8 iLoopY;

	for (iLoopX=0;iLoopX<18;iLoopX++)
	{
		for (iLoopY=0;iLoopY<18;iLoopY++)
		{
			PutTile(SCR_2_PLANE, 0, iLoopX+1, iLoopY+1, LevelData[LevelNumber].Background[iLoopY][iLoopX]);
		}
	}
}